import type { W3mCoreButton } from '@web3modal/ui'
import React from 'react'

/**
 * Component
 */
export function Web3Button(props: JSX.IntrinsicElements['w3m-core-button']) {
  return <w3m-core-button {...props} />
}

/**
 * Types
 */
declare global {
  namespace JSX {
    interface IntrinsicElements {
      'w3m-core-button': Pick<W3mCoreButton, 'avatar' | 'balance' | 'icon' | 'label'>
    }
  }
}
import type { W3mModal } from '@web3modal/ui'
import React from 'react'

/**
 * Component
 */
export function Modal(props: JSX.IntrinsicElements['w3m-modal']) {
  return <w3m-modal {...props} />
}

/**
 * Types
 */
declare global {
  namespace JSX {
    interface IntrinsicElements {
      'w3m-modal': Partial<W3mModal>
    }
  }
}
import type { ConfigCtrlState, ThemeCtrlState } from '@web3modal/core'
import { ClientCtrl, ConfigCtrl, OptionsCtrl, ThemeCtrl } from '@web3modal/core'
import type { EthereumClient } from '@web3modal/ethereum'
import React, { memo, useCallback, useEffect } from 'react'
import { Modal } from './Modal'

/**
 * Props
 */
export type Web3ModalProps = Omit<
  ConfigCtrlState,
  'enableStandaloneMode' | 'standaloneChains' | 'walletConnectVersion'
> &
  ThemeCtrlState & {
    ethereumClient?: EthereumClient
  }

/**
 * Component
 */
function CreateWeb3Modal({ ethereumClient, ...config }: Web3ModalProps) {
  const onConfigure = useCallback(async () => {
    ThemeCtrl.setThemeConfig(config)
    if (ethereumClient) {
      ClientCtrl.setEthereumClient(ethereumClient)
    }
    ConfigCtrl.setConfig({ ...config, walletConnectVersion: ethereumClient?.walletConnectVersion })
    await import('@web3modal/ui')
    OptionsCtrl.setIsUiLoaded(true)
  }, [ethereumClient, config])

  useEffect(() => {
    onConfigure()
  }, [onConfigure])

  return <Modal />
}

export const Web3Modal = memo(CreateWeb3Modal)
import type { W3mNetworkSwitch } from '@web3modal/ui'
import React from 'react'

/**
 * Component
 */
export function Web3NetworkSwitch(props: JSX.IntrinsicElements['w3m-network-switch']) {
  return <w3m-network-switch {...props} />
}

/**
 * Types
 */
declare global {
  namespace JSX {
    interface IntrinsicElements {
      'w3m-network-switch': Partial<W3mNetworkSwitch>
    }
  }
}
import { ModalCtrl, OptionsCtrl } from '@web3modal/core'
import { useEffect, useState } from 'react'

export function useWeb3Modal() {
  const [modal, setModal] = useState(ModalCtrl.state)

  useEffect(() => {
    const unsubscribe = ModalCtrl.subscribe(newModal => setModal({ ...newModal }))

    return () => {
      unsubscribe()
    }
  }, [])

  return {
    isOpen: modal.open,
    open: ModalCtrl.open,
    close: ModalCtrl.close,
    setDefaultChain: OptionsCtrl.setSelectedChain
  }
}
import { ThemeCtrl } from '@web3modal/core'
import { useEffect, useState } from 'react'

export function useWeb3ModalTheme() {
  const [config, setConfig] = useState({
    themeMode: ThemeCtrl.state.themeMode,
    themeVariables: ThemeCtrl.state.themeVariables
  })

  useEffect(() => {
    const unsubscribe = ThemeCtrl.subscribe(newTheme =>
      setConfig({
        themeMode: newTheme.themeMode,
        themeVariables: newTheme.themeVariables
      })
    )

    return () => {
      unsubscribe()
    }
  }, [])

  return {
    theme: config,
    setTheme: ThemeCtrl.setThemeConfig
  }
}
