// istanbul ignore next
const colorIfAllowed = (colorFn) => (text) => !process.env.NO_COLOR && process.env.NODE_ENV !== "production" ? colorFn(text) : text;
const red = colorIfAllowed((text) => `\x1B[31m${text}\x1B[39m`);
const yellow = colorIfAllowed((text) => `\x1B[33m${text}\x1B[39m`);
const green = colorIfAllowed((text) => `\x1B[32m${text}\x1B[39m`);
export const colors = { red, yellow, green };
